-- usuario
DROP TABLE IF EXISTS `usuario`;
CREATE TABLE IF NOT EXISTS `usuario` (
	`idusu` int(10) AUTO_INCREMENT NOT NULL COMMENT 'Identificador de usuario.',
	`idrol` varchar(3) NOT NULL COMMENT 'Identificador de rol de usuario.',
	`cedula` int(15) NOT NULL COMMENT 'Numero de cedula de identidad del usuario.',
	`nombre` varchar(50) NOT NULL COMMENT 'Nombres del usuario.',
	`apellido` varchar(50) NOT NULL COMMENT 'Apellidos del usuario.',
	`genero` varchar(15) NOT NULL COMMENT 'Genero del usuario.',
	`fecha_nacimiento` date NOT NULL COMMENT 'Fecha de nacimiento del usuario.',
	`nacionalidad` varchar(20) NOT NULL COMMENT 'Nacionalidad del usuario.',
	`direccion` varchar(50) NOT NULL COMMENT 'Dirección de residencia del usuario.',
	`telefono` varchar(15) NOT NULL COMMENT 'Teléfono de contacto.',
	`email` varchar(50) NOT NULL COMMENT 'Correo electrónico del usuario.',
	`user` varchar(15) NOT NULL COMMENT 'Nombre del usuario en la aplicación.',
	`pass` varchar(15) NOT NULL COMMENT 'Contraseña del usuario.',
	`fecha_registro` datetime NOT NULL COMMENT 'Fecha de creacion del registro.',
	`fecha_edicion` datetime NOT NULL COMMENT 'Fecha de ultima edicion del registro.',
	`estado_registro` int(1) NOT NULL COMMENT 'Codigo del estado del registro.',
	PRIMARY KEY (`idusu`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


-- codigo
DROP TABLE IF EXISTS `codigo`;
CREATE TABLE IF NOT EXISTS `codigo` (
	`idcod` int(11) AUTO_INCREMENT NOT NULL COMMENT 'Identificador de codigo fuente.',
	`idcodori` int(11) NULL COMMENT 'Identificador de codigo fuente padre.',
	`idusu` int(11) NOT NULL COMMENT 'Identificador de usuario.',
	`titulo` varchar(100) NOT NULL COMMENT 'Titulo del codigo fuente.',
	`descripcion` varchar(100) NOT NULL COMMENT 'Descripcion del codigo fuente.',
	`lenguaje` varchar(15) NOT NULL COMMENT 'Lenguaje de programacion del codigo fuente.',
	`codigo` varchar(10000) NOT NULL COMMENT 'Codigo fuente.',
	`vistas` int(11) NOT NULL COMMENT 'Cantidad de vistas realizadas al codigo fuente.',
	`descargas` int(11) NOT NULL COMMENT 'Cantidad de descargas realizadas al codigo fuente.',
	`ejecuciones` int(11) NOT NULL COMMENT 'Cantidad de ejecuciones realizadas al codigo fuente.',
	`estado_codigo` int(1) NOT NULL COMMENT 'Codigo del estado de aprobacion del registro.',
	`fecha_registro` datetime NOT NULL COMMENT 'Fecha de creacion del registro.',
	`fecha_edicion` datetime NOT NULL COMMENT 'Fecha de ultima edicion del registro.',
	`estado_registro` int(1) NOT NULL COMMENT 'Codigo del estado del registro.',
	PRIMARY KEY (`idcod`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


-- eventos
DROP TABLE IF EXISTS `evento`;
CREATE TABLE IF NOT EXISTS `evento` (
	`ideve` int(11) AUTO_INCREMENT NOT NULL COMMENT 'Identificador de evento.',
	`idusu` int(11) NOT NULL COMMENT '',
	`idcod` int(11) NOT NULL COMMENT 'Identificador de codigo fuente.',	
	`idcodori` int(11) NULL COMMENT 'Identificador de codigo fuente padre.',
	`accion` varchar(10) NOT NULL COMMENT '',
	`fecha_registro` datetime NOT NULL COMMENT 'Fecha de creacion del registro.',
	`fecha_edicion` datetime NOT NULL COMMENT 'Fecha de ultima edicion del registro.',
	`estado_registro` int(1) NOT NULL COMMENT 'Codigo del estado del registro.',
	PRIMARY KEY (`ideve`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


