(NULL, NULL, 1, 'Prueba de operadores logicos', 'Programa de prueba de operadores logicos en javascript', 'JavaScript', '<!DOCTYPE html>\r\n<html>\r\n    <head>\r\n        <title>Titulo de la pagina..</title>\r\n        <meta http-equiv="Content-Type" content="text/html; charset=utf-8"/>\r\n    </head>\r\n    <body>\r\n        <script type="text/javascript">\r\n        var valores = [true, 5, false, "hola", "adios", 2];\r\n         \r\n        // Cual de los 2 elementos de texto es mayor\r\n        // Si el resultado es true, el primer texto es mayor\r\n        var resultado = valores[3] > valores[4];\r\n        alert(resultado);\r\n         \r\n        // Combinar valores booleanos\r\n        var valor1 = valores[0];\r\n        var valor2 = valores[2];\r\n         \r\n        // Obtener un resultado TRUE\r\n        var resultado = valor1 || valor2;\r\n        alert(resultado);\r\n         \r\n        // Obtener un resultado FALSE\r\n        resultado = valor1 && valor2;\r\n        alert(resultado);\r\n         \r\n        // Operaciones matemÃ¡ticas\r\n        var num1 = valores[1];\r\n        var num2 = valores[5];\r\n         \r\n        var suma = num1 + num2;\r\n        alert(suma);\r\n         \r\n        var resta = num1 - num2;\r\n        alert(resta);\r\n         \r\n        var multiplicacion = num1 * num2;\r\n        alert(multiplicacion);\r\n         \r\n        var division = num1 / num2;\r\n        alert(division);\r\n         \r\n        var modulo = num1 % num2;\r\n        alert(modulo);\r\n         \r\n        </script>\r\n        <p>Esta pÃ¡gina muestra el uso bÃ¡sico de los operadores</p>\r\n    </body>\r\n</html>', 0, 0, 0, 0, NOW(), NOW(), 1),
