(NULL, NULL, 1, 'Limitar numero de caracteres en textarea en Javascript', 'Programa que limita la cantidad de caracteres en textarea', 'JavaScript', '<!DOCTYPE html>\r\n<html>\r\n    <head>\r\n        <title>Limitar nÃºmero de caracteres en textarea</title>\r\n        <meta http-equiv="Content-Type" content="text/html; charset=utf-8"/>\r\n    </head>\r\n    <body>\r\n        <script type="text/javascript">\r\n            function limita(elEvento, maximoCaracteres) {\r\n              var elemento = document.getElementById("texto");\r\n             \r\n              // Obtener la tecla pulsada \r\n              var evento = elEvento || window.event;\r\n              var codigoCaracter = evento.charCode || evento.keyCode;\r\n              // Permitir utilizar las teclas con flecha horizontal\r\n              if(codigoCaracter == 37 || codigoCaracter == 39) {\r\n                return true;\r\n              }\r\n             \r\n              // Permitir borrar con la tecla Backspace y con la tecla Supr.\r\n              if(codigoCaracter == 8 || codigoCaracter == 46) {\r\n                return true;\r\n              }\r\n              else if(elemento.value.length >= maximoCaracteres ) {\r\n                return false;\r\n              }\r\n              else {\r\n                return true;\r\n              }\r\n            }\r\n             \r\n            function actualizaInfo(maximoCaracteres) {\r\n              var elemento = document.getElementById("texto");\r\n              var info = document.getElementById("info");\r\n             \r\n              if(elemento.value.length >= maximoCaracteres ) {\r\n                info.innerHTML = "MÃ¡ximo "+maximoCaracteres+" caracteres";\r\n              }\r\n              else {\r\n                info.innerHTML = "Puedes escribir hasta "+(maximoCaracteres-elemento.value.length)+" caracteres adicionales";\r\n              }\r\n            }\r\n        </script>\r\n        \r\n        <div id="info">MÃ¡ximo 100 caracteres</div>\r\n        <textarea id="texto" onkeypress="return limita(event, 100);" onkeyup="actualizaInfo(100)" rows="6" cols="30"></textarea>\r\n    </body>\r\n</html>', 0, 0, 0, 0, NOW(), NOW(), 1),
