(NULL, NULL, 1, 'Metodos de ordenacion', 'Creo un vector de 20000 espacios, y los ordeno con todos los metodos de ordenacion populares', 'JavaScript', '//MENSAJE DEL ADMIN: NO SE ADMITEN CODIGOS NO JAVASCRIPT\r\n#include <stdio.h>\r\n#include <stdlib.h>\r\n#include <windows.h>\r\n#include <time.h>\r\n\r\nint tam=1000;\r\nclock_t inicio,final;\r\n\r\nvoid burbuja(int a[],int n,FILE *buffer);\r\nvoid shell(int a[],int n,FILE *buffer);\r\nint quicksort(int a[], int primero, int ultimo,FILE *buffer);\r\nvoid insercion(int a[],int n,FILE *buffer);\r\nvoid seleccion(int a[],int n,FILE *buffer);\r\nvoid merge(int a[],int bajo,int medio,int alto);\r\nvoid mergesort(int a[],int bajo,int alto,FILE *buffer);\r\n\r\nvoid guardar(FILE *buffer,int a[],int n);\r\nvoid mostrar(FILE *buffer);\r\nvoid mostrar2(int a[],int n);\r\nvoid menu();\r\nvoid borrar_archivo(FILE *buffer);\r\nint busquedaBin(int a[],int n);\r\n\r\nint main()\r\n{\r\n	FILE *buffer;\r\n	borrar_archivo(buffer);\r\n	\r\n	int tam2=tam-1;\r\n	int vector[tam];\r\n	int opcion;\r\n	//aqui relleno el vector de numeros random\r\n	srand(time(NULL));\r\n	for(int i=0;i<=tam;i++)\r\n	{\r\n		vector[i]=rand()%20001;\r\n		printf("\\n");\r\n	}\r\n\r\n	do\r\n	{\r\n		system("cls");\r\n		menu();\r\n		scanf("%d",&opcion);\r\n		switch(opcion)\r\n		{\r\n			case 1:\r\n				{\r\n					burbuja(vector,tam,buffer);\r\n					break;\r\n				}\r\n			case 2:\r\n				{\r\n					shell(vector,tam,buffer);\r\n					break;\r\n				}\r\n			case 3:\r\n				{\r\n					inicio=clock();\r\n\r\n					quicksort(vector,0,tam2,buffer);\r\n					\r\n					final=clock();\r\n\r\n					final=final-inicio;\r\n					\r\n					printf("\\n\\nel vector  fue ordenado...\\n\\n");\r\n    	 			Sleep(1000);\r\n					mostrar2(vector,tam);\r\n					guardar(buffer,vector,tam);\r\n					break;\r\n				}\r\n			case 4:\r\n				{\r\n					insercion(vector,tam,buffer);\r\n					break;\r\n				}\r\n			case 5:\r\n				{\r\n					seleccion(vector,tam,buffer);\r\n					break;\r\n				}\r\n			case 6:\r\n				{\r\n					mergesort(vector,0,tam-1,buffer);\r\n					printf("\\n\\nVector ordenado...");\r\n    				Sleep(1500);\r\n    				mostrar2(vector,tam);\r\n    				guardar(buffer,vector,tam);\r\n					break;\r\n				}\r\n			case 7:\r\n				{\r\n					busquedaBin(vector,tam);\r\n					break;\r\n				}\r\n			case 8:\r\n				{\r\n					mostrar(buffer);\r\n					break;\r\n				}\r\n			case 9:\r\n				{\r\n					mostrar2(vector,tam);\r\n					break;\r\n				}\r\n		}\r\n	}\r\n	while(opcion!=10);\r\n}\r\n\r\nvoid menu()\r\n{\r\n	printf("\\tMenu Principal");\r\n	printf("\\n1.-Ordenar por burbuja.");\r\n	printf("\\n2.-Ordenar por Shellsort.");\r\n	printf("\\n3.-Ordenar por Quicksort.");\r\n	printf("\\n4.-Ordenar por Insercion.");\r\n	printf("\\n5.-Ordenar por Seleccion.");\r\n	printf("\\n6.-Ordenar por Mergesort.");\r\n	printf("\\n7.-Buscar un numero en especifico(ordene primero).");\r\n	printf("\\n8.-Mostrar el archivo con los resultados(No Recomendado).");\r\n	printf("\\n9.-Mostrar el vector.");\r\n	printf("\\n10.-Salir.\\n\\n");\r\n}\r\n\r\nvoid mergesort(int a[], int bajo,int alto,FILE *buffer)\r\n{\r\n    if (bajo<alto) \r\n	{\r\n        int m = (alto+bajo)/2;\r\n        mergesort(a,bajo,m,buffer);\r\n        mergesort(a,m+1,alto,buffer);\r\n        merge(a,bajo,m,alto);\r\n    }\r\n}\r\n\r\nvoid merge(int a[],int bajo,int medio,int alto)\r\n{\r\n	int b[tam];\r\n	int i=bajo,j=medio+1, k=0;\r\n  \r\n    while (i<=medio && j<=alto) \r\n	{\r\n        if (a[i]<=a[j])\r\n        {\r\n            b[k++]=a[i++];\r\n    	}\r\n        else\r\n        {\r\n            b[k++]=a[j++];\r\n    	}\r\n    }\r\n    \r\n    while(i<=medio)\r\n    {\r\n        b[k++]=a[i++];\r\n	}\r\n	\r\n    while(j<=alto)\r\n    {\r\n        b[k++]=a[j++];\r\n	}\r\n	\r\n    k--;\r\n    \r\n    while(k>=0) \r\n	{\r\n        a[bajo + k]=b[k];\r\n        k--;\r\n    }\r\n}\r\n\r\nvoid seleccion(int a[],int n,FILE *buffer)\r\n{\r\n	int indicemenor,i,j;\r\n	///ordenar a[0]...a[n-2] y a[n-1] en cada pasada\r\n	for(i=0;i<n-1;i++)\r\n	{\r\n		//comienzo la exploracion del indice i\r\n		indicemenor=i;\r\n		//j explora la sublista a[i+1]...a[n-1]\r\n		for(j=i+1;j<n;j++)\r\n		{\r\n			if(a[j]<a[indicemenor])\r\n			{\r\n				indicemenor=j;\r\n				//situa el elemento mas pequeno en a[i]\r\n			}\r\n		}\r\n		if(i!=indicemenor)\r\n		{\r\n			int aux=a[i];\r\n			a[i]=a[indicemenor];\r\n			a[indicemenor]=aux;\r\n		}\r\n	}\r\n	printf("\\n\\nVector ordenado");\r\n	Sleep(1000);\r\n	mostrar2(a,n);\r\n	guardar(buffer,a,n);\r\n}\r\n\r\nvoid borrar_archivo(FILE *buffer)\r\n{\r\n	buffer=fopen("Ordenamiento.txt","w");\r\n	\r\n	if(buffer==NULL)\r\n	{\r\n		printf("ERROR....\\n\\n");\r\n	}\r\n	else\r\n	{	for(int f=0;f<90000;f++)\r\n		fprintf(buffer," ");\r\n		fclose(buffer);\r\n	}\r\n}\r\n\r\nvoid insercion(int a[],int n,FILE *buffer)\r\n{\r\n	int i,j,aux;\r\n	\r\n	for(i=1;i<n;i++)\r\n	{\r\n		//indice j  explora la  sublista a[i-1]...a[0] buscando la posicion correcta\r\n		//del elemento del destino, lo asigna a a[j]\r\n		j=i;\r\n		aux=a[i];\r\n		//se localiza el punto de insercion explorando hacia  abajo\r\n		while(j>0&&aux<a[j-1])\r\n		{\r\n			a[j]=a[j-1];\r\n			j--;\r\n		}\r\n		a[j]=aux;\r\n	}\r\n	printf("\\n\\nVector ordenado...\\n\\n");\r\n	Sleep(1500);\r\n	guardar(buffer,a,n);\r\n	mostrar2(a,n);\r\n}\r\n\r\nint quicksort(int a[], int primero, int ultimo,FILE *buffer)\r\n{\r\n    int i, j, central;\r\n    int pivote;\r\n    central = (primero + ultimo)/2;\r\n    pivote = a[central];\r\n    i = primero;\r\n    j = ultimo;\r\n\r\n    do \r\n    {\r\n      while (a[i] < pivote)\r\n	  {\r\n	      i++;\r\n	  }\r\n      while (a[j] > pivote) \r\n	  {\r\n          j--;\r\n      }\r\n      if (i<=j)\r\n        {\r\n          double tmp;\r\n          tmp = a[i];\r\n          a[i] = a[j];\r\n          a[j] = tmp; /* intercambia a[i] con a[j] */\r\n          i++;\r\n          j--;\r\n         }\r\n    }\r\n    while (i <= j);\r\n    \r\n    if (primero < j)\r\n    	quicksort(a, primero, j,buffer);/* mismo proceso con sublista izqda */\r\n    if (i < ultimo)\r\n        quicksort(a, i, ultimo,buffer); /* mismo proceso con sublista drcha */\r\n	return 0;\r\n}\r\n\r\nint busquedaBin(int a[],int n)\r\n{\r\n	int clave,central,bajo,alto,valorCentral;\r\n	system("cls");\r\n	printf("\\n\\nDame el numero a buscar : ");\r\n	scanf("%d",&clave);\r\n	\r\n	bajo=0;\r\n	alto=n-1;\r\n	while(bajo<=alto)\r\n	{\r\n		central=(bajo+alto)/2;\r\n		valorCentral=a[central];\r\n		if(clave==valorCentral)\r\n		{\r\n			printf("\\nEcontrado!\\nel numero esta en la posicion %d\\n\\n",central+1);\r\n			system("pause");\r\n			\r\n			return 0;\r\n		}\r\n		else if(clave<valorCentral)\r\n		{\r\n			alto=central-1;\r\n		}\r\n		else\r\n		{\r\n			bajo=central+1;\r\n		}\r\n	}\r\n	printf("\\nel numero  no fue encontrado...\\n\\n");\r\n	system("pause");\r\n	\r\n}\r\n\r\nvoid mostrar(FILE *buffer)\r\n{\r\n	char cadena[90000];\r\n	buffer=fopen("Ordenamiento.txt","r");\r\n	\r\n	if(buffer==NULL)\r\n	{\r\n		printf("\\nERROR...");\r\n	}\r\n	else\r\n	{\r\n		fgets(cadena,90000,buffer);\r\n		printf("%s\\n\\n\\n",cadena);\r\n		fclose(buffer);\r\n	}\r\n	system("pause");\r\n}\r\n\r\nvoid mostrar2(int a[],int n)\r\n{\r\n\r\n	for(int i=0;i<n;i++)\r\n	{\r\n		printf("\\nEn la posicion %d : %d",i+1,a[i]);\r\n	}\r\n	printf("\\nSe tardo %f ms\\n",final);\r\n	printf("\\n\\n");\r\n	system("pause");\r\n}\r\n\r\nvoid shell(int  a[],int n, FILE *buffer)\r\n{\r\n	int intervalo,i,j,k;	\r\n	intervalo=n/2;\r\n	while(intervalo>0)\r\n	{\r\n		for(i=intervalo;i<n;i++)\r\n		{\r\n			j=i-intervalo;\r\n			while(j>=0)\r\n			{\r\n				k=j+intervalo;\r\n				if(a[j]<=a[k])\r\n				{\r\n					j=-1;		//asi termina el bucle, par ordenado\r\n				}\r\n				else\r\n				{\r\n					int aux;\r\n					aux=a[j];\r\n					a[j]=a[k];\r\n					a[k]=aux;\r\n					j-=intervalo;\r\n				}\r\n			}\r\n		}\r\n		intervalo=intervalo/2;\r\n	}\r\n	guardar(buffer,a,n);\r\n	printf("\\n\\n");\r\n	mostrar2(a,n);\r\n}\r\n\r\nvoid guardar(FILE *buffer,int a[],int n)\r\n{\r\n	buffer=fopen("Ordenamiento.txt","w");\r\n	if(buffer==NULL)\r\n	{\r\n		printf("\\nERROR...");\r\n	}\r\n	else\r\n	{	fprintf(buffer,"\\n-------------------------------I");\r\n		for(int l=0;l<n;l++)\r\n		{\r\n			fprintf(buffer,"\\nEn la posicion %d : %d",l,a[l]);\r\n		}\r\n		fclose(buffer);\r\n	}\r\n}\r\n\r\nvoid burbuja(int a[],int n,FILE *buffer)\r\n{\r\n	int interruptor=0,pasada,j;\r\n	int aux;\r\n	\r\n	inicio=clock();\r\n\r\n	for(pasada=0;pasada<n;pasada++)\r\n	{\r\n		interruptor=0;\r\n		for(j=0;j<n-pasada-1;j++)\r\n		{\r\n			if(a[j]>a[j+1])\r\n			{\r\n				interruptor=1;\r\n				aux=a[j];\r\n				a[j]=a[j+1];\r\n				a[j+1]=aux;				\r\n			}\r\n		}\r\n	}\r\n	\r\n	final=clock();\r\n	\r\n	final=final-inicio;\r\n	Sleep(1000);\r\n	guardar(buffer,a,n);\r\n	mostrar2(a,n);\r\n}\r\n', 0, 0, 0, 0, NOW(), NOW(), 1),
